mapper.select:
select

from
    ns_core_resourcefield
where

    RESOURCEFIELD_FIELDLAZY = "1"

      and ENTERPRISE_ID = #{enterpriseId}


      and ORGANIZATION_ID = #{organizationId}


      and JE_CORE_RESOURCECOLUMN_ID = #{jeCoreResourcecolumnId}


      and RESOURCEFIELD_FUNCINFO_ID = #{funcId, jdbcType=VARCHAR}

 order by SY_ORDERINDEX ASC

select

from
    ns_core_resourcefield
where
    ENTERPRISE_ID = #{enterpriseId}
and ORGANIZATION_ID = #{organizationId}
and JE_CORE_RESOURCEFIELD_ID = #{jeCoreResourcefieldId}
order by SY_ORDERINDEX ASC

mapper.delete:
delete from ns_core_resourcefield 
where
    ENTERPRISE_ID = #{enterpriseId}
and	ORGANIZATION_ID = #{organizationId}

mapper.update:
update set ns_core_resourcefield
del_status = 1,
update_user_id = #{ updateUserId, jdbcType=BIGINT },
update_time = #{updateTime, jdbcType=TIMESTAMP }
where
	ID = #{id}
